"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import '../App.css';


const EditProfileModal = ({ isOpen, onClose, profile, onProfileUpdate }) => {
  const [bio, setBio] = useState("")
  const [profileImage, setProfileImage] = useState(null)
  const [previewImage, setPreviewImage] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (profile) {
      setBio(profile.bio || "")
      setPreviewImage(profile.profileImage || "")
    }
  }, [profile])

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setProfileImage(file)
      setPreviewImage(URL.createObjectURL(file))
    }
  }

const handleSubmit = async (e) => {
  e.preventDefault()
  setLoading(true)
  setError("")

  try {
    const token = localStorage.getItem("token") // o como estés guardando tu JWT

    // Actualizar biografía
    const bioResponse = await axios.put(
      "http://localhost:5000/apiÇ/users/bio",
      { bio },
      {
        headers: {
          Authorization: `Bearer ${token}`
        }
      }
    )

    // Actualizar imagen de perfil si se seleccionó una nueva
    let imageResponse = null
    if (profileImage) {
      const formData = new FormData()
      formData.append("profileImage", profileImage)

      imageResponse = await axios.put(
        "http://localhost:5000/api/users/profile-image",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`
          }
        }
      )
    }

    // Actualizar el perfil en el componente padre
    onProfileUpdate({
      bio: bioResponse.data.bio,
      profileImage: imageResponse ? imageResponse.data.profileImage : profile.profileImage,
    })

    onClose()
  } catch (error) {
    console.error("Error al actualizar perfil:", error)
    setError("Error al actualizar el perfil. Inténtalo de nuevo.")
  } finally {
    setLoading(false)
  }
}


  if (!isOpen) return null

  return (
    <div className="modal-overlay">
      <div className="edit-profile-modal">
        <div className="modal-header">
          <h2>Editar Perfil</h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Foto de Perfil</label>
            <div className="profile-image-upload">
              <img
                src={previewImage || "/placeholder.svg?height=150&width=150"}
                alt="Vista previa"
                className="preview-image"
              />
              <input type="file" accept="image/*" onChange={handleImageChange} />
            </div>
          </div>

          <div className="form-group">
            <label>Biografía</label>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Escribe algo sobre ti..."
              rows={4}
              maxLength={500}
            ></textarea>
            <small>{bio.length}/500 caracteres</small>
          </div>

          <div className="form-actions">
            <button type="button" className="cancel-button" onClick={onClose} disabled={loading}>
              Cancelar
            </button>
            <button type="submit" className="save-button" disabled={loading}>
              {loading ? "Guardando..." : "Guardar Cambios"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default EditProfileModal
