"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import axios from "axios"
import "../App.css"

const MessagesList = () => {
  const [conversations, setConversations] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        setLoading(true)
        const res = await axios.get("http://localhost:5000/api/messages/conversations")
        setConversations(res.data)
        setLoading(false)
      } catch (error) {
        console.error("Error al cargar conversaciones:", error)
        setError("Error al cargar conversaciones")
        setLoading(false)
      }
    }

    fetchConversations()
    
    // Actualizar cada 30 segundos
    const interval = setInterval(fetchConversations, 30000)
    return () => clearInterval(interval)
  }, [])

  if (loading) {
    return <div className="loading">Cargando conversaciones...</div>
  }

  if (error) {
    return <div className="error">{error}</div>
  }

  return (
    <div className="messages-container">
      <h1>Mensajes</h1>
      
      <div className="conversations-list">
        {conversations.length > 0 ? (
          conversations.map(conversation => (
            <Link 
              key={conversation.user_id}
              to={`/messages/${conversation.user_id}`}
              className="conversation-item"
            >
              <img 
                src={conversation.profileImage ? `http://localhost:5000${conversation.profileImage}` : "/placeholder.svg?height=50&width=50"} 
                alt={conversation.username}
                className="conversation-avatar"
              />
              <div className="conversation-info">
                <div className="conversation-header">
                  <h3 className="conversation-username">{conversation.username}</h3>
                  <span className="conversation-date">
                    {new Date(conversation.last_message_date).toLocaleDateString()}
                  </span>
                </div>
                {conversation.unread_count > 0 && (
                  <span className="unread-badge">{conversation.unread_count}</span>
                )}
              </div>
            </Link>
          ))
        ) : (
          <p className="no-conversations">No tienes conversaciones</p>
        )}
      </div>
    </div>
  )
}

export default MessagesList
