import React, { useState } from "react"
import axios from "axios"
import { FaBell } from "react-icons/fa"
import "../App.css" // (opcional para estilos)

function NotificationsList({ notifications = [], token }) {
  const [selectedPost, setSelectedPost] = useState(null)
  const [modalOpen, setModalOpen] = useState(false)
  const [openList, setOpenList] = useState(false)

  const unreadCount = notifications.filter(n => !n.isRead).length

  const toggleList = () => {
    setOpenList(!openList)
  }

  const fetchPost = async (postId) => {
    try {
      const res = await axios.get(`/notifications/post/${postId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      setSelectedPost(res.data)
      setModalOpen(true)
    } catch (error) {
      console.error("Error al obtener la publicación:", error)
    }
  }

  return (
    <div className="notification-wrapper" style={{ position: "relative" }}>
      <div onClick={toggleList} style={{ cursor: "pointer", position: "relative" }}>
        <FaBell size={20} />
        {unreadCount > 0 && (
          <span className="notification-badge">{unreadCount}</span>
        )}
      </div>

      {openList && (
        <ul className="notification-list">
          {notifications.map((notif) => (
            <li key={notif.id} onClick={() => fetchPost(notif.postId)}>
              {notif.type === "like" && `${notif.senderUsername} dio like a tu publicación`}
              {notif.type === "comment" && `${notif.senderUsername} comentó en tu publicación`}
            </li>
          ))}
        </ul>
      )}

      {modalOpen && selectedPost && (
        <div className="modal">
          <button onClick={() => setModalOpen(false)}>Cerrar</button>
          <h3>Publicación de {selectedPost.user.username}</h3>
          <p>{selectedPost.content}</p>
          {selectedPost.image && (
            <img src={selectedPost.image} alt="Post" style={{ maxWidth: "100%" }} />
          )}
          <p>Likes: {selectedPost.likes}</p>
          <p>Comentarios: {selectedPost.commentCount}</p>
        </div>
      )}
    </div>
  )
}

export default NotificationsList
