"use client"

import { useState, useEffect, useContext } from "react"
import { Link } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import styles from "../styles/NotificationList.module.css"

const NotificationList = () => {
  const {
    notifications,
    unreadCount,
    notificationsLoading,
    fetchNotifications,
    markNotificationAsRead,
    markAllNotificationsAsRead,
  } = useContext(AuthContext)

  const [showDropdown, setShowDropdown] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (showDropdown) {
      fetchNotifications().catch(() => {
        setError("No se pudieron cargar las notificaciones")
      })
    }
  }, [showDropdown, fetchNotifications])

  const handleMarkAsRead = async (id) => {
    try {
      await markNotificationAsRead(id)
    } catch (error) {
      console.error("Error al marcar notificación:", error)
    }
  }

  const handleMarkAllAsRead = async () => {
    try {
      await markAllNotificationsAsRead()
    } catch (error) {
      console.error("Error al marcar todas las notificaciones:", error)
    }
  }

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown)
    setError("")
  }

  const getNotificationContent = (notification) => {
    const { type, senderUsername } = notification
    switch (type) {
      case "like":
        return `${senderUsername} le dio like a tu publicación`
      case "comment":
        return `${senderUsername} comentó en tu publicación`
      case "follow":
        return `${senderUsername} comenzó a seguirte`
      default:
        return `Nueva notificación de ${senderUsername}`
    }
  }

  const getNotificationLink = (notification) => {
    const { type, postId, senderUsername } = notification
    switch (type) {
      case "like":
      case "comment":
        return `/post/${postId}`
      case "follow":
        return `/profile/${senderUsername}`
      default:
        return "#"
    }
  }

  const getNotificationIcon = (type) => {
    switch (type) {
      case "like":
        return <i className={`fas fa-heart ${styles.notificationIcon} ${styles.like}`}></i>
      case "comment":
        return <i className={`fas fa-comment ${styles.notificationIcon} ${styles.comment}`}></i>
      case "follow":
        return <i className={`fas fa-user-plus ${styles.notificationIcon} ${styles.follow}`}></i>
      default:
        return <i className={`fas fa-bell ${styles.notificationIcon}`}></i>
    }
  }

  const getImageUrl = (imagePath) => {
    if (!imagePath) return "/placeholder.svg?height=40&width=40"
    if (imagePath.startsWith("http")) return imagePath
    return `http://localhost:5000${imagePath}`
  }

  return (
    <div className={styles.notificationContainer}>
      <div className={styles.notificationBell} onClick={toggleDropdown}>
        <i className="fas fa-bell"></i>
        {unreadCount > 0 && <span className={styles.notificationBadge}>{unreadCount}</span>}
      </div>

      {showDropdown && (
        <div className={styles.notificationDropdown}>
          <div className={styles.notificationHeader}>
            <h3>Notificaciones</h3>
            {unreadCount > 0 && (
              <button className={styles.markAllRead} onClick={handleMarkAllAsRead}>
                Marcar todas como leídas
              </button>
            )}
          </div>

          {notificationsLoading ? (
            <div className={styles.notificationLoading}>Cargando...</div>
          ) : error ? (
            <div className={styles.notificationError}>{error}</div>
          ) : notifications.length > 0 ? (
            <div className={styles.notificationList}>
              {notifications.map((notification) => (
                <Link
                  key={notification.id}
                  to={getNotificationLink(notification)}
                  className={`${styles.notificationItem} ${
                    !notification.isRead ? styles.unread : ""
                  }`}
                  onClick={() => !notification.isRead && handleMarkAsRead(notification.id)}
                >
                  <div className={styles.notificationAvatar}>
                    <img
                      src={getImageUrl(notification.senderProfileImage)}
                      alt={notification.senderUsername}
                      className={styles.notificationUserImage}
                      onError={(e) => {
                        e.target.src = "/placeholder.svg?height=40&width=40"
                      }}
                    />
                    {getNotificationIcon(notification.type)}
                  </div>
                  <div className={styles.notificationContent}>
                    <p>{getNotificationContent(notification)}</p>
                    <span className={styles.notificationTime}>
                      {new Date(notification.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  {!notification.isRead && <div className={styles.notificationDot}></div>}
                </Link>
              ))}
            </div>
          ) : (
            <div className={styles.notificationEmpty}>No tienes notificaciones</div>
          )}

          <div className={styles.notificationFooter}>
            <Link to="/notifications" className={styles.viewAll}>
              Ver todas las notificaciones
            </Link>
          </div>
        </div>
      )}
    </div>
  )
}

export default NotificationList
