"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import "../App.css"

const BASE_URL = "http://localhost:5000"

const EditPostModal = ({ isOpen, onClose, post, onPostUpdate }) => {
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const [previewImage, setPreviewImage] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (post) {
      setContent(post.content || "")
      setPreviewImage(post.image?.startsWith("http")
        ? post.image
        : `${BASE_URL}${post.image}` || "")
    }
  }, [post])

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setImage(file)
      setPreviewImage(URL.createObjectURL(file))
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      const contentRes = await axios.put(`${BASE_URL}/api/posts/${post.id}`, { content })

      let imageRes = null
      if (image) {
        const formData = new FormData()
        formData.append("image", image)

        imageRes = await axios.put(`${BASE_URL}/api/posts/${post.id}/image`, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
      }

      onPostUpdate({
        ...post,
        content: contentRes.data.content,
        image: imageRes ? imageRes.data.image : post.image,
      })

      onClose()
    } catch (err) {
      console.error("Error updating post:", err)
      setError("No se pudo actualizar la publicación.")
    } finally {
      setLoading(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="modal-overlay">
      <div className="edit-post-modal">
        <div className="modal-header">
          <h2>Editar Publicación</h2>
          <button className="close-button" onClick={onClose}>&times;</button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Imagen</label>
            <div className="post-image-upload">
              <img
                src={previewImage || "/placeholder.svg?height=300"}
                alt="Vista previa"
                className="preview-image"
              />
              <input type="file" accept="image/*" onChange={handleImageChange} />
            </div>
          </div>

          <div className="form-group">
            <label>Contenido</label>
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="¿Qué estás pensando?"
              rows={5}
              maxLength={1000}
            />
            <small>{content.length}/1000 caracteres</small>
          </div>

          <div className="form-actions">
            <button type="button" className="cancel-button" onClick={onClose} disabled={loading}>
              Cancelar
            </button>
            <button type="submit" className="save-button" disabled={loading}>
              {loading ? "Guardando..." : "Guardar Cambios"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default EditPostModal
