"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import "./EditPostModal.css"

const EditPostModal = ({ isOpen, onClose, post, onPostUpdate }) => {
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const [previewImage, setPreviewImage] = useState("")
  const [keepExistingImage, setKeepExistingImage] = useState(true)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (post) {
      setContent(post.content || "")
      if (post.image) {
        setPreviewImage(getImageUrl(post.image))
      } else {
        setPreviewImage("")
      }
      setKeepExistingImage(!!post.image)
    }
  }, [post])

  // Función para construir la URL completa de la imagen
  const getImageUrl = (imagePath) => {
    if (!imagePath) return null

    // Si la ruta ya es una URL completa, devolverla tal cual
    if (imagePath.startsWith("http")) return imagePath

    // Construir la URL completa
    return `http://localhost:5000${imagePath}`
  }

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setImage(file)
      setPreviewImage(URL.createObjectURL(file))
      setKeepExistingImage(false)
    }
  }

  const handleRemoveImage = () => {
    setImage(null)
    setPreviewImage("")
    setKeepExistingImage(false)
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    if (!content && !keepExistingImage && !image) {
      setError("La publicación debe tener contenido o imagen")
      setLoading(false)
      return
    }

    try {
      const formData = new FormData()
      formData.append("content", content)

      // Si hay una nueva imagen, añadirla
      if (image) {
        formData.append("image", image)
      }
      // Si no hay nueva imagen y no queremos mantener la existente, enviar null
      else if (!keepExistingImage && post.image) {
        formData.append("image", "")
      }

      const res = await axios.put(`http://localhost:5000/api/posts/${post.id}`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      // Actualizar el post en el componente padre
      onPostUpdate({
        ...post,
        content: res.data.content,
        image: res.data.image,
      })

      onClose()
    } catch (error) {
      console.error("Error al actualizar publicación:", error)
      setError(error.response?.data?.message || "Error al actualizar la publicación")
    } finally {
      setLoading(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="modal-overlay">
      <div className="edit-post-modal">
        <div className="modal-header">
          <h2>Editar Publicación</h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Contenido</label>
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="¿Qué estás pensando?"
              rows={4}
            ></textarea>
          </div>

          <div className="form-group">
            <label>Imagen</label>
            {previewImage ? (
              <div className="image-preview-container">
                <img src={previewImage || "/placeholder.svg"} alt="Vista previa" className="image-preview" />
                <button type="button" className="remove-image-button" onClick={handleRemoveImage}>
                  Eliminar imagen
                </button>
              </div>
            ) : (
              <input type="file" accept="image/*" onChange={handleImageChange} />
            )}
          </div>

          <div className="form-actions">
            <button type="button" className="cancel-button" onClick={onClose} disabled={loading}>
              Cancelar
            </button>
            <button type="submit" className="save-button" disabled={loading}>
              {loading ? "Guardando..." : "Guardar Cambios"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default EditPostModal
