"use client"

import { createContext, useState, useEffect } from "react"
import axios from "axios"

export const AuthContext = createContext()

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const checkLoggedIn = async () => {
      try {
        const token = localStorage.getItem("token")
        if (token) {
          axios.defaults.headers.common["Authorization"] = `Bearer ${token}`
          const res = await axios.get("http://localhost:5000/api/auth/me")
          setUser(res.data)
        }
      } catch (error) {
        localStorage.removeItem("token")
        delete axios.defaults.headers.common["Authorization"]
      }
      setLoading(false)
    }

    checkLoggedIn()
  }, [])

  const login = async (email, password) => {
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      })
      localStorage.setItem("token", res.data.token)
      axios.defaults.headers.common["Authorization"] = `Bearer ${res.data.token}`
      setUser(res.data.user)
      return true
    } catch (error) {
      console.error("Error de login:", error)
      return false
    }
  }

  const register = async (username, email, password) => {
    try {
      // Imprimir los datos que se están enviando
      console.log("Enviando datos de registro:", { username, email, password: "***" })

      const res = await axios.post("http://localhost:5000/api/auth/register", {
        username,
        email,
        password,
      })

      console.log("Respuesta del servidor:", res.data)

      localStorage.setItem("token", res.data.token)
      axios.defaults.headers.common["Authorization"] = `Bearer ${res.data.token}`
      setUser(res.data.user)
      return { success: true }
    } catch (error) {
      console.error("Error de registro:", error)

      // Mostrar más detalles sobre el error
      if (error.response) {
        // El servidor respondió con un código de estado fuera del rango 2xx
        console.error("Datos de respuesta:", error.response.data)
        console.error("Estado:", error.response.status)
        console.error("Cabeceras:", error.response.headers)

        return {
          success: false,
          message: error.response.data?.message || "Error al registrarse. Verifica los datos e intenta nuevamente.",
          status: error.response.status,
        }
      } else if (error.request) {
        // La solicitud se hizo pero no se recibió respuesta
        console.error("No se recibió respuesta:", error.request)
        return {
          success: false,
          message: "No se recibió respuesta del servidor. Verifica tu conexión a internet.",
        }
      } else {
        // Algo sucedió al configurar la solicitud que desencadenó un error
        console.error("Error de configuración:", error.message)
        return {
          success: false,
          message: "Error al configurar la solicitud: " + error.message,
        }
      }
    }
  }

  const logout = () => {
    localStorage.removeItem("token")
    delete axios.defaults.headers.common["Authorization"]
    setUser(null)
  }

  return (
    <AuthContext.Provider
      value={{
        user,
        loading,
        login,
        register,
        logout,
      }}
    >
      {children}
    </AuthContext.Provider>
  )
}
