"use client"

import { useState, useEffect, useContext } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import Post from "../components/Post"
import EditPostMOdal from "../components/EditPostModal"
import "../App.css"

const Profile = () => {
  const { username } = useParams()
  const { user } = useContext(AuthContext)
  const [profile, setProfile] = useState(null)
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [isFollowing, setIsFollowing] = useState(false)

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${username}`)
        setProfile(res.data)
        setIsFollowing(res.data.isFollowing)

        const postsRes = await axios.get(`http://localhost:5000/api/posts/user/${username}`)
        setPosts(postsRes.data)

        setLoading(false)
      } catch (error) {
        setError("Error al cargar el perfil")
        setLoading(false)
        console.error("Error fetching profile:", error)
      }
    }

    fetchProfile()
  }, [username])

  const handleFollow = async () => {
    try {
      if (isFollowing) {
        await axios.delete(`http://localhost:5000/api/users/${profile.id}/unfollow`)
      } else {
        await axios.post(`http://localhost:5000/api/users/${profile.id}/follow`)
      }
      setIsFollowing(!isFollowing)
      setProfile({
        ...profile,
        followers: isFollowing ? profile.followers - 1 : profile.followers + 1,
      })
    } catch (error) {
      console.error("Error following/unfollowing user:", error)
    }
  }

  if (loading) {
    return <div className="loading">Cargando perfil...</div>
  }

  if (error) {
    return <div className="error">{error}</div>
  }

  if (!profile) {
    return <div className="error">Usuario no encontrado</div>
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-image-container">
          <img
            src={profile.profileImage || "/placeholder.svg?height=150&width=150"}
            alt={profile.username}
            className="profile-image"
          />
        </div>
        <div className="profile-info">
          <h1 className="profile-username">{profile.username}</h1>
          <div className="profile-stats">
            <div className="profile-stat">
              <span className="stat-count">{posts.length}</span>
              <span className="stat-label">Publicaciones</span>
            </div>
            <div className="profile-stat">
              <span className="stat-count">{profile.followers}</span>
              <span className="stat-label">Seguidores</span>
            </div>
            <div className="profile-stat">
              <span className="stat-count">{profile.following}</span>
              <span className="stat-label">Siguiendo</span>
            </div>
          </div>
          {user && user.id !== profile.id && (
            <button className={`follow-button ${isFollowing ? "following" : ""}`} onClick={handleFollow}>
              {isFollowing ? "Dejar de Seguir" : "Seguir"}
            </button>
          )}
        </div>
      </div>
      <div className="profile-bio">
        <p>{profile.bio || "Sin biografía"}</p>
      </div>
      <div className="profile-posts">
        <h2>Publicaciones</h2>
        <div className="posts-container">
          {posts.length > 0 ? (
            posts.map((post) => <Post key={post.id} post={post} />)
          ) : (
            <p>No hay publicaciones disponibles.</p>
          )}
        </div>
      </div>
    </div>
  )
}

export default Profile
