"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import Post from "../components/Post"

const Home = () => {
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [page, setPage] = useState(1)
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 5,
    total: 0,
    totalPages: 0,
  })

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        setLoading(true)
        const res = await axios.get(`http://localhost:5000/api/posts?page=${page}&limit=5`)
        setPosts(res.data.posts)
        setPagination(res.data.pagination)
        setLoading(false)
      } catch (error) {
        console.error("Error fetching posts:", error)
        setError("Error al cargar las publicaciones")
        setLoading(false)
      }
    }

    fetchPosts()
  }, [page])

  const handlePrevPage = () => {
    if (page > 1) {
      setPage(page - 1)
    }
  }

  const handleNextPage = () => {
    if (page < pagination.totalPages) {
      setPage(page + 1)
    }
  }

  if (loading && page === 1) {
    return <div className="loading">Cargando publicaciones...</div>
  }

  if (error) {
    return <div className="error">{error}</div>
  }

  return (
    <div className="home-container">
      <h1>Feed de Publicaciones</h1>
      <div className="posts-container">
        {posts.length > 0 ? (
          posts.map((post) => <Post key={post.id} post={post} />)
        ) : (
          <p>No hay publicaciones disponibles. ¡Sigue a más usuarios o crea tu primera publicación!</p>
        )}
      </div>

      {pagination.totalPages > 1 && (
        <div className="pagination">
          <button className="pagination-button" onClick={handlePrevPage} disabled={page === 1 || loading}>
            Anterior
          </button>
          <div className="pagination-info">
            Página {page} de {pagination.totalPages}
          </div>
          <button
            className="pagination-button"
            onClick={handleNextPage}
            disabled={page === pagination.totalPages || loading}
          >
            Siguiente
          </button>
        </div>
      )}
    </div>
  )
}

export default Home
