"use client"

import { useState, useEffect, useContext } from "react"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"  // <- Importa el contexto
import "../App.css"

const BASE_URL = "http://localhost:5000"

const EditProfileModal = ({ isOpen, onClose, profile }) => {
  const [bio, setBio] = useState("")
  const [profileImage, setProfileImage] = useState(null)
  const [previewImage, setPreviewImage] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  const { updateUser } = useContext(AuthContext)  // <- Destructuramos updateUser

  useEffect(() => {
    if (profile) {
      setBio(profile.bio || "")
      setPreviewImage(profile.profileImage ? `${BASE_URL}${profile.profileImage}` : "")
    }
  }, [profile])

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setProfileImage(file)
      setPreviewImage(URL.createObjectURL(file))
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      const bioResponse = await axios.put(`${BASE_URL}/api/users/bio`, { bio })

      let imageResponse = null
      if (profileImage) {
        const formData = new FormData()
        formData.append("profileImage", profileImage)
        imageResponse = await axios.put(`${BASE_URL}/api/users/profile-image`, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
      }

      // Actualizamos el contexto global con los nuevos datos
      updateUser({
        bio: bioResponse.data.bio,
        profileImage: imageResponse ? imageResponse.data.profileImage : profile.profileImage,
      })

      onClose()
    } catch (error) {
      console.error("Error al actualizar perfil:", error)
      setError("Error al actualizar el perfil. IntÃ©ntalo de nuevo.")
    } finally {
      setLoading(false)
    }
  }

  if (!isOpen) return null

  return (
    <div className="modal-overlay">
      <div className="edit-profile-modal">
        <div className="modal-header">
          <h2>Editar Perfil</h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Foto de Perfil</label>
            <div className="profile-image-upload">
              <img
                src={previewImage || "/placeholder.svg?height=150&width=150"}
                alt="Vista previa"
                className="preview-image"
              />
              <input type="file" accept="image/*" onChange={handleImageChange} />
            </div>
          </div>

          <div className="form-group">
            <label>BiografÃ­a</label>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Escribe algo sobre ti..."
              rows={4}
              maxLength={500}
            ></textarea>
            <small>{bio.length}/500 caracteres</small>
          </div>

          <div className="form-actions">
            <button type="button" className="cancel-button" onClick={onClose} disabled={loading}>
              Cancelar
            </button>
            <button type="submit" className="save-button" disabled={loading}>
              {loading ? "Guardando..." : "Guardar Cambios"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default EditProfileModal
