"use client"

import { useState, useEffect } from "react"
import "../App.css"

const AdminPosts = () => {
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [pagination, setPagination] = useState({ total: 0, pages: 1 })
  const [currentPage, setCurrentPage] = useState(1)

  useEffect(() => {
    fetchPosts(currentPage)
  }, [currentPage])

  const fetchPosts = async (page = 1) => {
    try {
      const token = localStorage.getItem("token")
      const response = await fetch(`http://localhost:5000/api/admin/posts?page=${page}&limit=10`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      console.log('Response status:', response.status)
      console.log('Response URL:', response.url)

      if (response.ok) {
        const data = await response.json()
        console.log('Posts data received:', data)
        
        // Tu backend devuelve un array directo
        if (Array.isArray(data)) {
          setPosts(data)
          setPagination({ total: data.length, pages: 1 })
        } else {
          setPosts([])
          setPagination({ total: 0, pages: 1 })
        }
      } else {
        const errorText = await response.text()
        console.error("Error en la respuesta:", response.status, errorText)
      }
    } catch (error) {
      console.error("Error fetching posts:", error)
    } finally {
      setLoading(false)
    }
  }

  const deletePost = async (postId) => {
    if (!window.confirm(`驴Est谩s seguro de que quieres eliminar esta publicaci贸n?`)) {
      return
    }

    try {
      const token = localStorage.getItem("token")
      const response = await fetch(`http://localhost:5000/api/admin/posts/${postId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (response.ok) {
        fetchPosts(currentPage)
        alert("Publicaci贸n eliminada correctamente")
      } else {
        alert("Error al eliminar la publicaci贸n")
      }
    } catch (error) {
      console.error("Error deleting post:", error)
      alert("Error al eliminar la publicaci贸n")
    }
  }

  const truncateContent = (content, maxLength = 100) => {
    if (content.length <= maxLength) return content
    return content.substring(0, maxLength) + "..."
  }

  if (loading) {
    return <div className="admin-loading">Cargando publicaciones...</div>
  }

  return (
    <div className="admin-posts">
      <div className="admin-header">
        <h1>Gesti贸n de Publicaciones</h1>
        <p>Total: {pagination?.total || 0} publicaciones</p>
        <button onClick={() => fetchPosts(currentPage)} className="admin-btn">
          Recargar
        </button>
      </div>

      <div className="posts-list">
        {posts && posts.length > 0 ? (
          posts.map((post) => (
            <div key={post.id} className="post-card">
              <div className="post-header">
                <div className="post-info">
                  <span className="post-id">ID: {post.id}</span>
                  <span className="post-author">Usuario ID: {post.user_id}</span>
                  <span className="post-date">{new Date(post.created_at).toLocaleDateString()}</span>
                </div>
                <div className="post-stats">
                  {post.image && <span className="has-image"> Con imagen</span>}
                </div>
              </div>

              <div className="post-content">
                <p>{truncateContent(post.content)}</p>
                {post.image && (
                  <div className="post-image">
                    <img src={`http://localhost:5000${post.image}`} alt="Post" style={{maxWidth: '200px', maxHeight: '200px'}} />
                  </div>
                )}
              </div>

              <div className="post-actions">
                <button className="action-btn delete" onClick={() => deletePost(post.id)}>
                  Eliminar Publicaci贸n
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-data">
            <p>No hay publicaciones para mostrar.</p>
          </div>
        )}
      </div>

      <div className="pagination">
        <button disabled={currentPage === 1} onClick={() => setCurrentPage(currentPage - 1)}>
          Anterior
        </button>
        <span>
          P谩gina {currentPage} de {pagination?.pages || 1}
        </span>
        <button disabled={currentPage === (pagination?.pages || 1)} onClick={() => setCurrentPage(currentPage + 1)}>
          Siguiente
        </button>
      </div>
    </div>
  )
}

export default AdminPosts
