"use client"

import { useContext, useState, useEffect } from "react"
import { Link, useNavigate } from "react-router-dom"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import SearchBar from "./SearchBar"
import { FaBell } from "react-icons/fa"

const Navbar = () => {
  const { user, logout } = useContext(AuthContext)
  const [unreadNotifications, setUnreadNotifications] = useState(0)
  const navigate = useNavigate()

  useEffect(() => {
    if (user) {
      fetchUnreadNotificationsCount()
    }
  }, [user])

  const fetchUnreadNotificationsCount = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/notifications/unread-count")
      setUnreadNotifications(res.data.count)
    } catch (error) {
      console.error("Error al obtener conteo de notificaciones:", error)
    }
  }

  const handleLogout = () => {
    logout()
    navigate("/login")
  }

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          SocialApp
        </Link>

        {user && <SearchBar />}

        {user ? (
          <ul className="navbar-menu">
            <li className="navbar-item">
              <Link to="/" className="navbar-link">
                Inicio
              </Link>
            </li>
            <li className="navbar-item">
              <Link to="/create-post" className="navbar-link">
                Crear Post
              </Link>
            </li>
            <li className="navbar-item">
              <Link to="/notifications" className="navbar-link notification-link" style={{ position: "relative", display: "inline-block" }}>
                <FaBell size={20} />
                {unreadNotifications > 0 && (
                  <span className="notification-badge">{unreadNotifications}</span>
                )}
              </Link>
              </li>
            <li className="navbar-item">
              <Link to={`/profile/${user.username}`} className="navbar-link">
                Perfil
              </Link>
            </li>
            <li className="navbar-item">
              <button onClick={handleLogout} className="navbar-button">
                Cerrar Sesi√≥n
              </button>
            </li>
          </ul>
        ) : (
          <ul className="navbar-menu">
            <li className="navbar-item">
              <Link to="/login" className="navbar-link">
                Iniciar Sesi√≥n
              </Link>
            </li>
            <li className="navbar-item">
              <Link to="/register" className="navbar-link">
                Registrarse
              </Link>
            </li>
          </ul>
        )}
      </div>
    </nav>
  )
}

export default Navbar
