"use client"

import { useState, useContext, useEffect, useRef } from "react"
import { Link, useLocation } from "react-router-dom"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import EditPostModal from "./EditPostModal"

const Post = ({ post, onPostUpdate, onPostDelete }) => {
  const { user } = useContext(AuthContext)
  const location = useLocation()
  const commentIdToHighlight = location.state?.commentId

  const [likes, setLikes] = useState(post.likes || 0)
  const [liked, setLiked] = useState(post.liked || false)
  const [showComments, setShowComments] = useState(false)
  const [commentText, setCommentText] = useState("")
  const [comments, setComments] = useState(post.comments || [])
  const [commentCount, setCommentCount] = useState(post.commentCount || post.comments?.length || 0)
  const [loadingComments, setLoadingComments] = useState(false)
  const [allCommentsLoaded, setAllCommentsLoaded] = useState(false)
  const [imageError, setImageError] = useState(false)
  const [isEditModalOpen, setIsEditModalOpen] = useState(false)
  const [isDeleting, setIsDeleting] = useState(false)
  const [showOptions, setShowOptions] = useState(false)

  const commentRefs = useRef({})

  const isOwner = user && post.user && user.id === post.user.id

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null
    if (imagePath.startsWith("http")) return imagePath
    return `http://localhost:5000${imagePath}`
  }

  const handleLike = async () => {
    try {
      await axios.post(`http://localhost:5000/api/posts/${post.id}/like`)
      setLiked(!liked)
      setLikes(liked ? likes - 1 : likes + 1)
    } catch (error) {
      console.error("Error liking post:", error)
    }
  }

  const handleComment = async (e) => {
    e.preventDefault()
    if (!commentText.trim()) return

    try {
      const res = await axios.post(`http://localhost:5000/api/posts/${post.id}/comment`, {
        content: commentText,
      })
      setComments([res.data, ...comments])
      setCommentCount(commentCount + 1)
      setCommentText("")
    } catch (error) {
      console.error("Error commenting on post:", error)
    }
  }

  const toggleComments = async () => {
    setShowComments(!showComments)

    if (showComments) return

    if (comments.length === 0) {
      try {
        setLoadingComments(true)
        const res = await axios.get(`http://localhost:5000/api/posts/${post.id}/comments?limit=5`)
        setComments(res.data.comments)
        setAllCommentsLoaded(res.data.comments.length >= res.data.pagination.total)
        setLoadingComments(false)
      } catch (error) {
        console.error("Error loading comments:", error)
        setLoadingComments(false)
      }
    }
  }

  const loadMoreComments = async () => {
    try {
      setLoadingComments(true)
      const page = Math.floor(comments.length / 5) + 1
      const res = await axios.get(`http://localhost:5000/api/posts/${post.id}/comments?page=${page}&limit=5`)
      setComments([...comments, ...res.data.comments])
      setAllCommentsLoaded(comments.length + res.data.comments.length >= res.data.pagination.total)
      setLoadingComments(false)
    } catch (error) {
      console.error("Error loading more comments:", error)
      setLoadingComments(false)
    }
  }

  const handleImageError = () => {
    console.error("Error al cargar la imagen:", post.image)
    setImageError(true)
  }

  const handleEditPost = () => {
    setIsEditModalOpen(true)
    setShowOptions(false)
  }

  const handleDeletePost = async () => {
    if (window.confirm("¿Estás seguro de que quieres eliminar esta publicación?")) {
      try {
        setIsDeleting(true)
        await axios.delete(`http://localhost:5000/api/posts/${post.id}`)
        if (onPostDelete) onPostDelete(post.id)
      } catch (error) {
        console.error("Error al eliminar publicación:", error)
        alert("Error al eliminar la publicación")
      } finally {
        setIsDeleting(false)
      }
    }
  }

  const handlePostUpdate = (updatedPost) => {
    setImageError(false)
    if (onPostUpdate) onPostUpdate(updatedPost)
  }

  const toggleOptions = () => {
    setShowOptions(!showOptions)
  }

  const setCommentRef = (id, node) => {
    if (node) commentRefs.current[id] = node
  }

  useEffect(() => {
    if (showComments && commentIdToHighlight && commentRefs.current[commentIdToHighlight]) {
      commentRefs.current[commentIdToHighlight].scrollIntoView({ behavior: "smooth", block: "center" })
    }
  }, [showComments, comments])

  useEffect(() => {
    if (commentIdToHighlight && !showComments) toggleComments()
  }, [])

  return (
    <div className="post">
      <div className="post-header">
        <Link to={`/profile/${post.user.username}`} className="post-user">
          <img
            src={getImageUrl(post.user.profileImage) || "/placeholder.svg?height=40&width=40"}
            alt={post.user.username}
            className="post-user-image"
            onError={(e) => {
              e.target.src = "/placeholder.svg?height=40&width=40"
            }}
          />
          <span className="post-username">{post.user.username}</span>
        </Link>
        <div className="post-header-right">
          <span className="post-date">{new Date(post.createdAt).toLocaleDateString()}</span>
          {isOwner && (
            <div className="post-options">
              <button className="post-options-button" onClick={toggleOptions}>⋮</button>
              {showOptions && (
                <div className="post-options-menu">
                  <button onClick={handleEditPost} disabled={isDeleting}>Editar</button>
                  <button onClick={handleDeletePost} disabled={isDeleting}>
                    {isDeleting ? "Eliminando..." : "Eliminar"}
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="post-content">
        <p>{post.content}</p>
        {post.image && !imageError && (
          <div className="post-image-container">
            <img
              src={getImageUrl(post.image) || "/placeholder.svg"}
              alt="Post"
              className="post-image"
              onError={handleImageError}
            />
          </div>
        )}
        {imageError && (
          <div className="post-image-error">
            <p>No se pudo cargar la imagen</p>
            <p className="post-image-path">Ruta: {post.image}</p>
          </div>
        )}
      </div>

      <div className="post-actions">
        <button className={`post-like-button ${liked ? "liked" : ""}`} onClick={handleLike}>
          {liked ? "❤️" : "🤍"} {likes}
        </button>
        <button className="post-comment-button" onClick={toggleComments}>
          💬 {commentCount}
        </button>
      </div>

      {showComments && (
        <div className="post-comments">
          <form onSubmit={handleComment} className="comment-form">
            <input
              type="text"
              placeholder="Escribe un comentario..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              className="comment-input"
            />
            <button type="submit" className="comment-submit">Enviar</button>
          </form>

          {loadingComments && comments.length === 0 ? (
            <div className="loading">Cargando comentarios...</div>
          ) : (
            <>
              <div className="comment-list">
                {comments.map((comment) => (
                  <div
                    key={comment.id}
                    ref={(el) => setCommentRef(comment.id, el)}
                    className={`comment-item ${comment.id === commentIdToHighlight ? "highlight-comment" : ""}`}
                  >
                    <p>{comment.content}</p>
                    <span>— {comment.authorUsername}</span>
                  </div>
                ))}
              </div>

              {!allCommentsLoaded && commentCount > comments.length && (
                <div className="view-all-comments">
                  <button
                    className="view-all-comments-button"
                    onClick={loadMoreComments}
                    disabled={loadingComments}
                  >
                    {loadingComments
                      ? "Cargando..."
                      : `Ver más comentarios (${commentCount - comments.length} restantes)`}
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      )}

      {isEditModalOpen && (
        <EditPostModal
          isOpen={isEditModalOpen}
          onClose={() => setIsEditModalOpen(false)}
          post={post}
          onPostUpdate={handlePostUpdate}
        />
      )}
    </div>
  )
}

export default Post
