"use client"

import { useState, useEffect, useContext } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import Post from "../components/Post"
import EditProfileModal from "../components/EditProfileModal"

const BASE_URL = "http://localhost:5000"

const getImageUrl = (imagePath) => {
  if (!imagePath) return null
  return imagePath.startsWith("http") ? imagePath : `${BASE_URL}${imagePath}`
}

const Profile = () => {
  const { username } = useParams()
  const { user } = useContext(AuthContext)
  const [profile, setProfile] = useState(null)
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [isFollowing, setIsFollowing] = useState(false)
  const [isEditModalOpen, setIsEditModalOpen] = useState(false)

  const isOwnProfile = user && profile && user.id === profile.id

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/api/users/${username}`)
        setProfile(res.data)
        setIsFollowing(res.data.isFollowing)

        const postsRes = await axios.get(`${BASE_URL}/api/posts/user/${username}`)
        setPosts(postsRes.data)
      } catch (error) {
        console.error("Error fetching profile:", error)
        setError("Error al cargar el perfil")
      } finally {
        setLoading(false)
      }
    }

    fetchProfile()
  }, [username])

  const handleFollow = async () => {
    try {
      if (isFollowing) {
        await axios.delete(`${BASE_URL}/api/users/${profile.id}/unfollow`)
      } else {
        await axios.post(`${BASE_URL}/api/users/${profile.id}/follow`)
      }
      setIsFollowing(!isFollowing)
      setProfile({
        ...profile,
        followers: isFollowing ? profile.followers - 1 : profile.followers + 1,
      })
    } catch (error) {
      console.error("Error following/unfollowing user:", error)
    }
  }

  const handleProfileUpdate = (updatedData) => {
    setProfile({ ...profile, ...updatedData })
  }

  const handlePostUpdate = (updatedPost) => {
    setPosts((prev) => prev.map((post) => (post.id === updatedPost.id ? { ...post, ...updatedPost } : post)))
  }

  const handlePostDelete = (postId) => {
    setPosts((prev) => prev.filter((post) => post.id !== postId))
  }

  if (loading) return <div className="loading">Cargando perfil...</div>
  if (error) return <div className="error">{error}</div>
  if (!profile) return <div className="error">Usuario no encontrado</div>

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-image-container">
          <img
            src={getImageUrl(profile.profileImage) || "/placeholder.svg?height=150&width=150"}
            alt={profile.username}
            className="profile-image"
            onError={(e) => (e.target.src = "/placeholder.svg?height=150&width=150")}
          />
          {isOwnProfile && (
            <button onClick={() => setIsEditModalOpen(true)}>Cambiar foto</button>
          )}
        </div>

        <div className="profile-info">
          <h1>{profile.username}</h1>
          {isOwnProfile && (
            <button onClick={() => setIsEditModalOpen(true)}>Editar perfil</button>
          )}
          <div className="profile-stats">
            <div><b>{profile.posts}</b> Publicaciones</div>
            <div><b>{profile.followers}</b> Seguidores</div>
            <div><b>{profile.following}</b> Siguiendo</div>
            <div><b>{profile.comments}</b> Comentarios</div>
            <div><b>{profile.likes}</b> Me gusta</div>
          </div>
          {user && user.id !== profile.id && (
            <button className={`follow-button ${isFollowing ? "following" : ""}`} onClick={handleFollow}>
              {isFollowing ? "Dejar de Seguir" : "Seguir"}
            </button>
          )}
        </div>
      </div>

      <div className="profile-bio">
        <p>{profile.bio || "Sin biografÃ­a"}</p>
      </div>

      <div className="profile-posts">
        <h2>Publicaciones</h2>
        {posts.length > 0 ? (
          posts.map((post) => (
            <Post
              key={post.id}
              post={post}
              onPostUpdate={handlePostUpdate}
              onPostDelete={handlePostDelete}
            />
          ))
        ) : (
          <p>No hay publicaciones disponibles.</p>
        )}
      </div>

      {isOwnProfile && (
        <EditProfileModal
          isOpen={isEditModalOpen}
          onClose={() => setIsEditModalOpen(false)}
          profile={profile}
          onProfileUpdate={handleProfileUpdate}
        />
      )}
    </div>
  )
}

export default Profile
