"use client"

import { useState } from "react"
import { useNavigate } from "react-router-dom"
import axios from "axios"

const CreatePost = () => {
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const [imagePreview, setImagePreview] = useState(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const navigate = useNavigate()

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      // Verificar el tamaño del archivo (máximo 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError("La imagen no debe superar los 5MB")
        return
      }

      // Verificar el tipo de archivo
      const validTypes = ["image/jpeg", "image/jpg", "image/png", "image/gif"]
      if (!validTypes.includes(file.type)) {
        setError("Solo se permiten imágenes en formato JPEG, JPG, PNG o GIF")
        return
      }

      setImage(file)
      setError("")

      // Crear preview
      const reader = new FileReader()
      reader.onloadend = () => {
        setImagePreview(reader.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!content.trim() && !image) {
      setError("Por favor, añada texto o una imagen a su publicación")
      return
    }

    setLoading(true)
    setError("")

    try {
      // Crear FormData para enviar la imagen
      const formData = new FormData()
      formData.append("content", content)

      if (image) {
        formData.append("image", image)
      }

      // Mostrar lo que se está enviando (para depuración)
      console.log("Enviando contenido:", content)
      console.log("Enviando imagen:", image)

      // Realizar la petición con el tipo correcto
      const response = await axios.post("http://localhost:5000/api/posts", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      console.log("Respuesta del servidor:", response.data)

      setLoading(false)
      navigate("/")
    } catch (error) {
      setLoading(false)
      console.error("Error completo:", error)

      if (error.response) {
        // El servidor respondió con un código de estado fuera del rango 2xx
        setError(`Error: ${error.response.data.message || "Error al crear la publicación"}`)
        console.error("Datos de respuesta:", error.response.data)
      } else if (error.request) {
        // La petición fue hecha pero no se recibió respuesta
        setError("No se recibió respuesta del servidor. Verifique su conexión.")
      } else {
        // Algo ocurrió al configurar la petición
        setError(`Error: ${error.message}`)
      }
    }
  }

  return (
    <div className="create-post-container">
      <h1>Crear Nueva Publicación</h1>
      {error && <div className="create-post-error">{error}</div>}
      <form onSubmit={handleSubmit} className="create-post-form">
        <div className="form-group">
          <label htmlFor="content">¿Qué estás pensando?</label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Escribe algo..."
            rows="4"
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="image">Añadir Imagen (máx. 5MB - JPG, PNG, GIF)</label>
          <input
            type="file"
            id="image"
            accept="image/jpeg,image/jpg,image/png,image/gif"
            onChange={handleImageChange}
          />
          {imagePreview && (
            <div className="image-preview">
              <img src={imagePreview || "/placeholder.svg"} alt="Preview" />
            </div>
          )}
        </div>
        <button type="submit" className="create-post-button" disabled={loading}>
          {loading ? "Publicando..." : "Publicar"}
        </button>
      </form>
    </div>
  )
}

export default CreatePost
