"use client"

import { useState, useEffect } from "react"
import { useAuth } from "../context/AuthContext"
import "../App.css"

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || ""

const AdminUsers = () => {
  const { isSuperAdmin } = useAuth()
  const [users, setUsers] = useState([])
  const [loading, setLoading] = useState(true)
  const [pagination, setPagination] = useState({})
  const [currentPage, setCurrentPage] = useState(1)

  useEffect(() => {
    fetchUsers(currentPage)
  }, [currentPage])

  async function fetchUsers(page = 1) {
    const token = localStorage.getItem("token")
    try {
      const data = await fetchData(`/api/admin/users?page=${page}&limit=10`, token)
      setUsers(data.users)
      setPagination(data.pagination)
    } catch (error) {
      console.error("Error fetching users:", error)
    } finally {
      setLoading(false)
    }
  }

  async function deleteUser(userId, username) {
    if (!window.confirm(`Â¿Seguro que quieres eliminar al usuario "${username}"?`)) return

    const token = localStorage.getItem("token")
    try {
      const response = await fetch(`${API_BASE_URL}/api/admin/users/${userId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      })

      if (response.ok) {
        fetchUsers(currentPage)
        alert("Usuario eliminado correctamente")
      } else {
        const errorData = await response.json()
        alert(`Error: ${errorData.error || "Error desconocido"}`)
      }
    } catch (error) {
      console.error("Error deleting user:", error)
      alert("Error al eliminar el usuario")
    }
  }

  if (loading) return <div className="admin-loading">Cargando usuarios...</div>

  return (
    <div className="admin-users">
      <div className="admin-header">
        <h1>GestiÃ³n de Usuarios</h1>
        <p>Total: {pagination.total || 0} usuarios</p>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Usuario</th>
              <th>Email</th>
              <th>Rol</th>
              <th>Posts</th>
              <th>Comentarios</th>
              <th>Fecha Registro</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>
                  <span className={`role-badge ${user.role}`}>{user.role}</span>
                </td>
                <td>{user.post_count}</td>
                <td>{user.comment_count}</td>
                <td>{new Date(user.created_at).toLocaleDateString()}</td>
                <td>
                  <div className="user-actions">
                    {isSuperAdmin() && user.role !== "admin" && (
                      <button className="action-btn delete" onClick={() => deleteUser(user.id, user.username)}>
                        Eliminar
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="pagination">
        <button disabled={currentPage === 1} onClick={() => setCurrentPage(currentPage - 1)}>
          Anterior
        </button>
        <span>
          PÃ¡gina {currentPage} de {pagination.pages || 1}
        </span>
        <button disabled={currentPage === pagination.pages} onClick={() => setCurrentPage(currentPage + 1)}>
          Siguiente
        </button>
      </div>
    </div>
  )
}

async function fetchData(endpoint, token) {
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || ""
  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  })
  if (!response.ok) {
    throw new Error(`Error ${response.status}: ${response.statusText}`)
  }
  return response.json()
}

export default AdminUsers
