"use client"

import { useState, useEffect, useContext } from "react"
import { Link } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import "../App.css"

const NotificationList = () => {
  // ✅ Usar AuthContext en lugar de contexto separado
  const {
    notifications,
    unreadCount,
    notificationsLoading,
    fetchNotifications,
    markNotificationAsRead,
    markAllNotificationsAsRead,
  } = useContext(AuthContext)

  const [showDropdown, setShowDropdown] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (showDropdown) {
      fetchNotifications().catch(() => {
        setError("No se pudieron cargar las notificaciones")
      })
    }
  }, [showDropdown, fetchNotifications])

  const handleMarkAsRead = async (id) => {
    try {
      await markNotificationAsRead(id)
    } catch (error) {
      console.error("Error al marcar notificación:", error)
    }
  }

  const handleMarkAllAsRead = async () => {
    try {
      await markAllNotificationsAsRead()
    } catch (error) {
      console.error("Error al marcar todas las notificaciones:", error)
    }
  }

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown)
    setError("")
  }

  const getNotificationContent = (notification) => {
    const { type, senderUsername } = notification

    switch (type) {
      case "like":
        return `${senderUsername} le dio like a tu publicación`
      case "comment":
        return `${senderUsername} comentó en tu publicación`
      case "follow":
        return `${senderUsername} comenzó a seguirte`
      default:
        return `Nueva notificación de ${senderUsername}`
    }
  }

  const getNotificationLink = (notification) => {
    const { type, postId, senderUsername } = notification

    switch (type) {
      case "like":
      case "comment":
        return `/post/${postId}`
      case "follow":
        return `/profile/${senderUsername}`
      default:
        return "#"
    }
  }

  const getNotificationIcon = (type) => {
    switch (type) {
      case "like":
        return <i className="fas fa-heart notification-icon like"></i>
      case "comment":
        return <i className="fas fa-comment notification-icon comment"></i>
      case "follow":
        return <i className="fas fa-user-plus notification-icon follow"></i>
      default:
        return <i className="fas fa-bell notification-icon"></i>
    }
  }

  const getImageUrl = (imagePath) => {
    if (!imagePath) return "/placeholder.svg?height=40&width=40"
    if (imagePath.startsWith("http")) return imagePath
    return `http://localhost:5000${imagePath}`
  }

  return (
    <div className="notification-container">
      <div className="notification-bell" onClick={toggleDropdown}>
        <i className="fas fa-bell"></i>
        {unreadCount > 0 && <span className="notification-badge">{unreadCount}</span>}
      </div>

      {showDropdown && (
        <div className="notification-dropdown">
          <div className="notification-header">
            <h3>Notificaciones</h3>
            {unreadCount > 0 && (
              <button className="mark-all-read" onClick={handleMarkAllAsRead}>
                Marcar todas como leídas
              </button>
            )}
          </div>

          {notificationsLoading ? (
            <div className="notification-loading">Cargando...</div>
          ) : error ? (
            <div className="notification-error">{error}</div>
          ) : notifications.length > 0 ? (
            <div className="notification-list">
              {notifications.map((notification) => (
                <Link
                  key={notification.id}
                  to={getNotificationLink(notification)}
                  className={`notification-item ${!notification.isRead ? "unread" : ""}`}
                  onClick={() => !notification.isRead && handleMarkAsRead(notification.id)}
                >
                  <div className="notification-avatar">
                    <img
                      src={getImageUrl(notification.senderProfileImage) || "/placeholder.svg"}
                      alt={notification.senderUsername}
                      className="notification-user-image"
                      onError={(e) => {
                        e.target.src = "/placeholder.svg?height=40&width=40"
                      }}
                    />
                    {getNotificationIcon(notification.type)}
                  </div>
                  <div className="notification-content">
                    <p>{getNotificationContent(notification)}</p>
                    <span className="notification-time">{new Date(notification.createdAt).toLocaleDateString()}</span>
                  </div>
                  {!notification.isRead && <div className="notification-dot"></div>}
                </Link>
              ))}
            </div>
          ) : (
            <div className="notification-empty">No tienes notificaciones</div>
          )}

          <div className="notification-footer">
            <Link to="/notifications" className="view-all">
              Ver todas las notificaciones
            </Link>
          </div>
        </div>
      )}
    </div>
  )
}

export default NotificationList
