"use client"

import { useState } from "react"

const Avatar = ({ src, username, size = 40, className = "" }) => {
  const [error, setError] = useState(false)

  const getImageUrl = (imagePath) => {
    if (!imagePath || error) return `/placeholder.svg?height=${size}&width=${size}`

    if (imagePath.startsWith("http")) return imagePath

    return `http://localhost:5000${imagePath}`
  }

  return (
    <img
      src={getImageUrl(src) || "/placeholder.svg"}
      alt={username || "Usuario"}
      className={`avatar-image ${className}`}
      style={{ width: `${size}px`, height: `${size}px`, borderRadius: "50%", objectFit: "cover" }}
      onError={() => setError(true)}
    />
  )
}

export default Avatar
