import { useState, useContext } from "react"
import EditPostModal from "./EditPostModal"  // Importa tu modal de edici√≥n
// ... otros imports

const Post = ({ post }) => {
  const { user } = useContext(AuthContext)

  // Estados que ya tienes...
  const [likes, setLikes] = useState(post.likes || 0)
  // ...
  const [showEditModal, setShowEditModal] = useState(false)
  const [currentPost, setCurrentPost] = useState(post)

  // Funci√≥n para abrir modal de edici√≥n
  const handleEditClick = () => {
    setShowEditModal(true)
  }

  // Funci√≥n para cerrar modal de edici√≥n
  const handleCloseModal = () => {
    setShowEditModal(false)
  }

  // Funci√≥n que se llamar√° desde el modal cuando se guarden los cambios
  const handleSaveEdit = (updatedPost) => {
    setCurrentPost(updatedPost)
    setShowEditModal(false)
  }

  // Funci√≥n para eliminar el post
  const handleDelete = async () => {
    if (!window.confirm("¬øSeguro que quieres eliminar este post?")) return

    try {
      await axios.delete(`http://localhost:5000/api/posts/${post.id}`)
      alert("Post eliminado correctamente")
      // Aqu√≠ puedes agregar l√≥gica para actualizar la lista de posts, 
      // o recargar la p√°gina, o emitir evento, seg√∫n tu arquitectura
    } catch (error) {
      console.error("Error eliminando post:", error)
      alert("Error al eliminar el post")
    }
  }

  // Solo mostrar botones editar/eliminar si el usuario es due√±o del post
  const isOwner = user && user.id === post.user.id

  // En el render, usa currentPost en lugar de post para mostrar contenido actualizado

  return (
    <div className="post">
      <div className="post-header">
        <Link to={`/profile/${currentPost.user.username}`} className="post-user">
          <Avatar 
            src={currentPost.user.profileImage} 
            username={currentPost.user.username} 
            size={40}
            className="post-user-image"
          />
          <span className="post-username">{currentPost.user.username}</span>
        </Link>
        <span className="post-date">{new Date(currentPost.createdAt).toLocaleDateString()}</span>
      </div>
      <div className="post-content">
        <p>{currentPost.content}</p>
        {currentPost.image && !imageError && (
          <div className="post-image-container">
            <img
              src={getImageUrl(currentPost.image) || "/placeholder.svg"}
              alt="Post"
              className="post-image"
              onError={handleImageError}
            />
          </div>
        )}
        {imageError && (
          <div className="post-image-error">
            <p>No se pudo cargar la imagen</p>
            <p className="post-image-path">Ruta: {currentPost.image}</p>
          </div>
        )}
      </div>
      <div className="post-actions">
        <button className={`post-like-button ${liked ? "liked" : ""}`} onClick={handleLike}>
          {liked ? "‚ù§Ô∏è" : "ü§ç"} {likes}
        </button>
        <button className="post-comment-button" onClick={toggleComments}>
          üí¨ {commentCount}
        </button>

        {isOwner && (
          <>
            <button onClick={handleEditClick} className="post-edit-button">
              ‚úèÔ∏è Editar
            </button>
            <button onClick={handleDelete} className="post-delete-button">
              üóëÔ∏è Eliminar
            </button>
          </>
        )}
      </div>

      {/* Comentarios */}
      {showComments && (
        <div className="post-comments">
          {/* ... tu c√≥digo de comentarios */}
        </div>
      )}

      {/* Modal de edici√≥n */}
      {showEditModal && (
        <EditPostModal
          post={currentPost}
          onClose={handleCloseModal}
          onSave={handleSaveEdit}
        />
      )}
    </div>
  )
}

export default Post
