import React, { useState } from "react"
import axios from "axios"

function NotificationsList({ notifications= [], token }) {
  const [selectedPost, setSelectedPost] = useState(null)
  const [modalOpen, setModalOpen] = useState(false)

  const fetchPost = async (postId) => {
    try {
      const res = await axios.get(`/notifications/post/${postId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      setSelectedPost(res.data)
      setModalOpen(true)
    } catch (error) {
      console.error("Error al obtener la publicación:", error)
    }
  }

  return (
    <div>
      <h2>Notificaciones</h2>
      <ul>
        {notifications.map((notif) => (
          <li key={notif.id} onClick={() => fetchPost(notif.post_id)} style={{cursor: "pointer"}}>
            {notif.type === "like" && `${notif.sender_username} dio like a tu publicación`}
            {notif.type === "comment" && `${notif.sender_username} comentó en tu publicación`}
          </li>
        ))}
      </ul>

      {modalOpen && selectedPost && (
        <div className="modal">
          <button onClick={() => setModalOpen(false)}>Cerrar</button>
          <h3>Publicación de {selectedPost.user.username}</h3>
          <p>{selectedPost.content}</p>
          {selectedPost.image && <img src={selectedPost.image} alt="Post" style={{maxWidth: "100%"}} />}
          <p>Likes: {selectedPost.likes}</p>
          <p>Comentarios: {selectedPost.commentCount}</p>
          {/* Puedes mostrar más info o comentarios aquí */}
        </div>
      )}
    </div>
  )
}

export default NotificationsList
