"use client"

import { useState, useEffect, useRef } from "react"
import { Link } from "react-router-dom"
import axios from "axios"
import "../App.css"

const NotificationDropdown = () => {
  const [notifications, setNotifications] = useState([])
  const [loading, setLoading] = useState(false)
  const [isOpen, setIsOpen] = useState(false)
  const [unreadCount, setUnreadCount] = useState(0)
  const dropdownRef = useRef(null)

  // Cargar notificaciones
  const fetchNotifications = async () => {
    try {
      setLoading(true)
      const res = await axios.get("http://localhost:5000/api/notifications")
      
      // Verificar si res.data es un array antes de usar filter()
      if (Array.isArray(res.data)) {
        setNotifications(res.data)
        setUnreadCount(res.data.filter(notif => !notif.isRead).length)
      } else {
        console.error("La respuesta no es un array:", res.data)
        setNotifications([]) // Manejar la respuesta no v谩lida
        setUnreadCount(0)
      }
      setLoading(false)
    } catch (error) {
      console.error("Error al cargar notificaciones:", error)
      setLoading(false)
      setNotifications([]) // Manejar el error y establecer las notificaciones a un array vac铆o
      setUnreadCount(0)
    }
  }

  // Cargar notificaciones al montar el componente
  useEffect(() => {
    fetchNotifications()

    // Configurar intervalo para actualizar notificaciones
    const interval = setInterval(fetchNotifications, 30000) // Cada 30 segundos
    return () => clearInterval(interval)
  }, [])

  // Cerrar el dropdown al hacer clic fuera
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false)
      }
    }

    document.addEventListener("mousedown", handleClickOutside)
    return () => document.removeEventListener("mousedown", handleClickOutside)
  }, [])

  // Marcar notificaci贸n como le铆da
  const handleNotificationClick = async (notification) => {
    if (!notification.isRead) {
      try {
        await axios.put(`http://localhost:5000/api/notifications/${notification.id}/read`)
        
        // Actualizar estado local
        setNotifications(notifications.map(n => 
          n.id === notification.id ? { ...n, isRead: true } : n
        ))
        setUnreadCount(prev => Math.max(0, prev - 1))
      } catch (error) {
        console.error("Error al marcar notificaci贸n como le铆da:", error)
      }
    }
  }

  // Marcar todas como le铆das
  const markAllAsRead = async () => {
    try {
      await axios.put("http://localhost:5000/api/notifications/read-all")
      
      // Actualizar estado local
      setNotifications(notifications.map(n => ({ ...n, isRead: true })))
      setUnreadCount(0)
    } catch (error) {
      console.error("Error al marcar todas como le铆das:", error)
    }
  }

  // Generar enlace y texto seg煤n el tipo de notificaci贸n
  const getNotificationDetails = (notification) => {
    switch (notification.type) {
      case 'like':
        return {
          link: `/post/${notification.post.id}`,
          text: `dio me gusta a tu publicaci贸n`
        }
      case 'comment':
        return {
          link: `/post/${notification.post.id}`,
          text: `coment贸 en tu publicaci贸n`
        }
      case 'follow':
        return {
          link: `/profile/${notification.sender.username}`,
          text: `comenz贸 a seguirte`
        }
      case 'message':
        return {
          link: `/messages/${notification.sender.id}`,
          text: `te envi贸 un mensaje`
        }
      default:
        return {
          link: '/',
          text: 'interactu贸 con tu contenido'
        }
    }
  }

  return (
    <div className="notification-container" ref={dropdownRef}>
      <button 
        className="notification-button" 
        onClick={() => setIsOpen(!isOpen)}
      >
        <i className="notification-icon"></i>
        {unreadCount > 0 && (
          <span className="notification-badge">{unreadCount}</span>
        )}
      </button>

      {isOpen && (
        <div className="notification-dropdown">
          <div className="notification-header">
            <h3>Notificaciones</h3>
            {unreadCount > 0 && (
              <button className="mark-all-read" onClick={markAllAsRead}>
                Marcar todas como le铆das
              </button>
            )}
          </div>

          <div className="notification-list">
            {loading ? (
              <div className="notification-loading">Cargando...</div>
            ) : notifications.length > 0 ? (
              notifications.map(notification => {
                const { link, text } = getNotificationDetails(notification)
                return (
                  <Link 
                    key={notification.id}
                    to={link}
                    className={`notification-item ${!notification.isRead ? 'unread' : ''}`}
                    onClick={() => handleNotificationClick(notification)}
                  >
                    <img 
                      src={notification.sender.profileImage ? `http://localhost:5000${notification.sender.profileImage}` : "/placeholder.svg?height=40&width=40"} 
                      alt={notification.sender.username}
                      className="notification-avatar"
                    />
                    <div className="notification-content">
                      <p>
                        <strong>{notification.sender.username}</strong> {text}
                      </p>
                      <span className="notification-time">
                        {new Date(notification.createdAt).toLocaleString()}
                      </span>
                    </div>
                  </Link>
                )
              })
            ) : (
              <div className="notification-empty">No tienes notificaciones</div>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

export default NotificationDropdown
