"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import axios from "axios"
import "../App.css"

const Notifications = () => {
  const [notifications, setNotifications] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0,
  })
  // Estado para el modal
  const [selectedNotification, setSelectedNotification] = useState(null)
  const [showModal, setShowModal] = useState(false)

  const navigate = useNavigate()

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        setLoading(true)
        const res = await axios.get(
          `http://localhost:5000/api/notifications?page=${pagination.page}&limit=${pagination.limit}`,
        )
        setNotifications(res.data.notifications)
        setPagination(res.data.pagination)
        setLoading(false)
      } catch (error) {
        console.error("Error al cargar notificaciones:", error)
        setError("No se pudieron cargar las notificaciones")
        setLoading(false)
      }
    }

    fetchNotifications()
  }, [pagination.page, pagination.limit])

  const markAsRead = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/notifications/${id}/read`)

      // Actualizar estado local
      setNotifications(notifications.map((notif) => (notif.id === id ? { ...notif, isRead: true } : notif)))
    } catch (error) {
      console.error("Error al marcar notificación:", error)
    }
  }

  const markAllAsRead = async () => {
    try {
      await axios.put("http://localhost:5000/api/notifications/read-all")

      // Actualizar estado local
      setNotifications(notifications.map((notif) => ({ ...notif, isRead: true })))
    } catch (error) {
      console.error("Error al marcar todas las notificaciones:", error)
    }
  }

  const getNotificationContent = (notification) => {
    const { type, senderUsername } = notification

    switch (type) {
      case "like":
        return `${senderUsername} le dio like a tu publicación`
      case "comment":
        return `${senderUsername} comentó en tu publicación`
      case "follow":
        return `${senderUsername} comenzó a seguirte`
      default:
        return `Nueva notificación de ${senderUsername}`
    }
  }

 /* const getNotificationLink = (notification) => {
    const { type, postId, senderUsername } = notification

    switch (type) {
      case "like":
      case "comment":
        return `/post/${postId}`
      case "follow":
        return `/profile/${senderUsername}`
      default:
        return "#"
    }
  }*/

  const getNotificationIcon = (type) => {
    switch (type) {
      case "like":
        return <i className="fas fa-heart notification-icon like"></i>
      case "comment":
        return <i className="fas fa-comment notification-icon comment"></i>
      case "follow":
        return <i className="fas fa-user-plus notification-icon follow"></i>
      default:
        return <i className="fas fa-bell notification-icon"></i>
    }
  }

  // Función para manejar las URLs de imágenes
  const getImageUrl = (imagePath) => {
    if (!imagePath) return "/placeholder.svg?height=40&width=40"

    // Si la ruta ya es una URL completa, devolverla tal cual
    if (imagePath.startsWith("http")) return imagePath

    // Construir la URL completa
    return `http://localhost:5000${imagePath}`
  }

  // Función para abrir el modal con la notificación seleccionada
  const openNotificationModal = (notification) => {
    if (!notification.isRead) {
      markAsRead(notification.id)
    }
    setSelectedNotification(notification)
    setShowModal(true)
  }

  // Función para cerrar el modal
  const closeModal = () => {
    setShowModal(false)
    setSelectedNotification(null)
  }

  // Función para navegar al perfil del usuario que envió la notificación
  const navigateToUserProfile = () => {
    if (selectedNotification) {
      closeModal()
      navigate(`/profile/${selectedNotification.senderUsername}`)
    }
  }

  return (
    <div className="notifications-page">
      <div className="notifications-header">
        <h1>Notificaciones</h1>
        <button className="mark-all-read-btn" onClick={markAllAsRead}>
          Marcar todas como leídas
        </button>
      </div>

      {loading ? (
        <div className="notifications-loading">Cargando notificaciones...</div>
      ) : error ? (
        <div className="notifications-error">{error}</div>
      ) : notifications.length > 0 ? (
        <div className="notifications-list">
          {notifications.map((notification) => (
            <div
              key={notification.id}
              className={`notification-item ${!notification.isRead ? "unread" : ""}`}
              onClick={() => openNotificationModal(notification)}
            >
              <div className="notification-avatar">
                <img
                  src={getImageUrl(notification.senderProfileImage) || "/placeholder.svg?height=40&width=40"}
                  alt={notification.senderUsername}
                  onError={(e) => {
                    e.target.src = "/placeholder.svg?height=40&width=40"
                  }}
                />
                {getNotificationIcon(notification.type)}
              </div>
              <div className="notification-content">
                <p>{getNotificationContent(notification)}</p>
                <span className="notification-time">
                  {new Date(notification.createdAt).toLocaleDateString()}{" "}
                  {new Date(notification.createdAt).toLocaleTimeString()}
                </span>
              </div>
              {!notification.isRead && <div className="notification-dot"></div>}
            </div>
          ))}
        </div>
      ) : (
        <div className="notifications-empty">No tienes notificaciones</div>
      )}

      {pagination.totalPages > 1 && (
        <div className="notifications-pagination">
          <button
            className="pagination-button"
            onClick={() => setPagination({ ...pagination, page: pagination.page - 1 })}
            disabled={pagination.page === 1 || loading}
          >
            Anterior
          </button>
          <div className="pagination-info">
            Página {pagination.page} de {pagination.totalPages}
          </div>
          <button
            className="pagination-button"
            onClick={() => setPagination({ ...pagination, page: pagination.page + 1 })}
            disabled={pagination.page === pagination.totalPages || loading}
          >
            Siguiente
          </button>
        </div>
      )}

      {/* Modal de notificación */}
      {showModal && selectedNotification && (
        <div className="notification-modal-overlay" onClick={closeModal}>
          <div className="notification-modal" onClick={(e) => e.stopPropagation()}>
            <div className="notification-modal-header">
              <h3>Notificación</h3>
              <button className="close-modal" onClick={closeModal}>
                ×
              </button>
            </div>
            <div className="notification-modal-content">
              <div className="notification-modal-user">
                <img
                  src={getImageUrl(selectedNotification.senderProfileImage) || "/placeholder.svg?height=60&width=60"}
                  alt={selectedNotification.senderUsername}
                  className="notification-modal-avatar"
                  onError={(e) => {
                    e.target.src = "/placeholder.svg?height=60&width=60"
                  }}
                />
                <div className="notification-modal-user-info">
                  <h4>{selectedNotification.senderUsername}</h4>
                  <p className="notification-modal-time">
                    {new Date(selectedNotification.createdAt).toLocaleDateString()}{" "}
                    {new Date(selectedNotification.createdAt).toLocaleTimeString()}
                  </p>
                </div>
              </div>
              <div className="notification-modal-message">
                <p>{getNotificationContent(selectedNotification)}</p>
              </div>
              <div className="notification-modal-actions">
                <button className="notification-modal-btn profile" onClick={navigateToUserProfile}>
                  Ver perfil de {selectedNotification.senderUsername}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default Notifications
