"use client"

import { useState, useEffect } from "react"
import "../App.css"

const AdminComments = () => {
  const [comments, setComments] = useState([])
  const [loading, setLoading] = useState(true)
  const [pagination, setPagination] = useState({ total: 0, pages: 1 })
  const [currentPage, setCurrentPage] = useState(1)
  const [users, setUsers] = useState({}) // Para almacenar los nombres de usuario

  useEffect(() => {
    fetchComments(currentPage)
  }, [currentPage])

  // Función para obtener el nombre de usuario por ID
  const fetchUsername = async (userId) => {
    if (users[userId]) return users[userId] // Si ya lo tenemos, no hacer otra petición

    try {
      const token = localStorage.getItem("token")
      const response = await fetch(`http://localhost:5000/api/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (response.ok) {
        const userData = await response.json()
        setUsers(prev => ({ ...prev, [userId]: userData.username }))
        return userData.username
      }
    } catch (error) {
      console.error("Error fetching username:", error)
    }
    return `Usuario ${userId}` // Fallback si no se puede obtener el nombre
  }

  const fetchComments = async (page = 1) => {
    try {
      const token = localStorage.getItem("token")
      const response = await fetch(`http://localhost:5000/api/admin/comments?page=${page}&limit=10`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      console.log('Response status:', response.status)
      console.log('Response URL:', response.url)

      if (response.ok) {
        const data = await response.json()
        console.log('Comments data received:', data)
        
        if (Array.isArray(data)) {
          setComments(data)
          setPagination({ total: data.length, pages: 1 })
          
          // Obtener nombres de usuario para todos los comentarios
          data.forEach(comment => {
            fetchUsername(comment.user_id)
          })
        } else {
          setComments([])
          setPagination({ total: 0, pages: 1 })
        }
      } else {
        const errorText = await response.text()
        console.error("Error en la respuesta:", response.status, errorText)
      }
    } catch (error) {
      console.error("Error fetching comments:", error)
    } finally {
      setLoading(false)
    }
  }

  const deleteComment = async (commentId) => {
    if (!window.confirm(`¿Estás seguro de que quieres eliminar este comentario?`)) {
      return
    }

    try {
      const token = localStorage.getItem("token")
      const response = await fetch(`http://localhost:5000/api/admin/comments/${commentId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (response.ok) {
        fetchComments(currentPage)
        alert("Comentario eliminado correctamente")
      } else {
        alert("Error al eliminar el comentario")
      }
    } catch (error) {
      console.error("Error deleting comment:", error)
      alert("Error al eliminar el comentario")
    }
  }

  if (loading) {
    return <div className="admin-loading">Cargando comentarios...</div>
  }

  return (
    <div className="admin-comments">
      <div className="admin-header">
        <h1>Gestión de Comentarios</h1>
        <p>Total: {pagination?.total || 0} comentarios</p>
        <button onClick={() => fetchComments(currentPage)} className="admin-btn">
          Recargar
        </button>
      </div>

      <div className="comments-list">
        {comments && comments.length > 0 ? (
          comments.map((comment) => (
            <div key={comment.id} className="comment-card">
              <div className="comment-header">
                <div className="comment-info">
                  <span className="comment-id">ID: {comment.id}</span>
                  <span className="comment-author">Por: {users[comment.user_id] || `Usuario ${comment.user_id}`}</span>
                  <span className="comment-date">{new Date(comment.created_at).toLocaleDateString()}</span>
                </div>
              </div>

              <div className="comment-content">
                <p>
                  <strong>Comentario:</strong> {comment.content}
                </p>
                <p>
                  <strong>En publicación ID:</strong> {comment.post_id}
                </p>
              </div>

              <div className="comment-actions">
                <button className="action-btn delete" onClick={() => deleteComment(comment.id)}>
                  Eliminar Comentario
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-data">
            <p>No hay comentarios para mostrar.</p>
          </div>
        )}
      </div>

      <div className="pagination">
        <button disabled={currentPage === 1} onClick={() => setCurrentPage(currentPage - 1)}>
          Anterior
        </button>
        <span>
          Página {currentPage} de {pagination?.pages || 1}
        </span>
        <button disabled={currentPage === (pagination?.pages || 1)} onClick={() => setCurrentPage(currentPage + 1)}>
          Siguiente
        </button>
      </div>
    </div>
  )
}

export default AdminComments
