import { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";  // <-- Importa useParams
import axios from "axios";
import Post from "../components/Post";

const Home = () => {
  const { postId } = useParams(); // <-- Obtén postId de la URL
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // Ref para almacenar referencias a los posts
  const postsRefs = useRef({});

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`http://localhost:5000/api/posts?page=${page}&limit=10`);
      setPosts(res.data.posts);
      setTotalPages(res.data.pagination.totalPages);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching posts:", error);
      setError("Error al cargar las publicaciones");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [page]);

  // Hacer scroll cuando los posts cambien o cambie postId
  useEffect(() => {
    if (postId && posts.length > 0) {
      // Esperar un tick para que el DOM esté actualizado
      setTimeout(() => {
        const postElement = postsRefs.current[postId];
        if (postElement) {
          postElement.scrollIntoView({ behavior: "smooth", block: "start" });
        }
      }, 100);
    }
  }, [postId, posts]);

  const handlePostUpdate = (updatedPost) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) => (post.id === updatedPost.id ? { ...post, ...updatedPost } : post))
    );
  };

  const handlePostDelete = (postId) => {
    setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId));
  };

  const handlePrevPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  const handleNextPage = () => {
    if (page < totalPages) {
      setPage(page + 1);
    }
  };

  if (loading && posts.length === 0) {
    return <div className="loading">Cargando publicaciones...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="home-container">
      <h1>Inicio</h1>
      <div className="posts-container">
        {posts.length > 0 ? (
          posts.map((post) => (
            <div
              key={post.id}
              ref={(el) => (postsRefs.current[post.id] = el)}  // <-- asigna ref a cada post
            >
              <Post post={post} onPostUpdate={handlePostUpdate} onPostDelete={handlePostDelete} />
            </div>
          ))
        ) : (
          <p className="no-posts">No hay publicaciones disponibles.</p>
        )}
      </div>
      {totalPages > 1 && (
        <div className="pagination">
          <button className="pagination-button" onClick={handlePrevPage} disabled={page === 1}>
            Anterior
          </button>
          <span className="pagination-info">
            Página {page} de {totalPages}
          </span>
          <button className="pagination-button" onClick={handleNextPage} disabled={page === totalPages}>
            Siguiente
          </button>
        </div>
      )}
    </div>
  );
};

export default Home;
