"use client"

import { useState, useEffect } from "react"
import "../App.css"

const AdminComments = () => {
  const [comments, setComments] = useState([])
  const [loading, setLoading] = useState(true)
  const [pagination, setPagination] = useState({})
  const [currentPage, setCurrentPage] = useState(1)

  useEffect(() => {
    fetchComments(currentPage)
  }, [currentPage])

  const fetchComments = async (page = 1) => {
    try {
      const token = localStorage.getItem("token")
      // URL corregida para ser consistente
      const response = await fetch(`http://localhost:5000/api/admin/comments?page=${page}&limit=10`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      console.log('Response status:', response.status)
      console.log('Response URL:', response.url)

      if (response.ok) {
        const data = await response.json()
        console.log('Comments data received:', data)
        setComments(data.comments)
        setPagination(data.pagination)
      } else {
        const errorText = await response.text()
        console.error("Error en la respuesta:", response.status, errorText)
      }
    } catch (error) {
      console.error("Error fetching comments:", error)
    } finally {
      setLoading(false)
    }
  }

  const deleteComment = async (commentId, username) => {
    if (!window.confirm(`¿Estás seguro de que quieres eliminar este comentario de ${username}?`)) {
      return
    }

    try {
      const token = localStorage.getItem("token")
      // URL corregida para ser consistente
      const response = await fetch(`http://localhost:5000/api/admin/comments/${commentId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (response.ok) {
        fetchComments(currentPage)
        alert("Comentario eliminado correctamente")
      } else {
        alert("Error al eliminar el comentario")
      }
    } catch (error) {
      console.error("Error deleting comment:", error)
      alert("Error al eliminar el comentario")
    }
  }

  const truncateContent = (content, maxLength = 80) => {
    if (content.length <= maxLength) return content
    return content.substring(0, maxLength) + "..."
  }

  if (loading) {
    return <div className="admin-loading">Cargando comentarios...</div>
  }

  return (
    <div className="admin-comments">
      <div className="admin-header">
        <h1>Gestión de Comentarios</h1>
        <p>Total: {pagination.total} comentarios</p>
      </div>

      <div className="comments-list">
        {comments.map((comment) => (
          <div key={comment.id} className="comment-card">
            <div className="comment-header">
              <div className="comment-info">
                <span className="comment-id">ID: {comment.id}</span>
                <span className="comment-author">Por: {comment.username}</span>
                <span className="comment-date">{new Date(comment.created_at).toLocaleDateString()}</span>
              </div>
            </div>

            <div className="comment-content">
              <p>
                <strong>Comentario:</strong> {comment.content}
              </p>
              <p>
                <strong>En publicación:</strong> {truncateContent(comment.post_content)}
              </p>
            </div>

            <div className="comment-actions">
              <button className="action-btn delete" onClick={() => deleteComment(comment.id, comment.username)}>
                Eliminar Comentario
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="pagination">
        <button disabled={currentPage === 1} onClick={() => setCurrentPage(currentPage - 1)}>
          Anterior
        </button>
        <span>
          Página {currentPage} de {pagination.pages}
        </span>
        <button disabled={currentPage === pagination.pages} onClick={() => setCurrentPage(currentPage + 1)}>
          Siguiente
        </button>
      </div>
    </div>
  )
}

export default AdminComments
