"use client"

import { useState, useEffect, useContext, useRef } from "react"
import { useParams, useLocation } from "react-router-dom"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import Post from "../components/Post"
import EditProfileModal from "../components/EditProfileModal"

const BASE_URL = "http://localhost:5000"

const Profile = () => {
  const { username } = useParams()
  const location = useLocation()
  const query = new URLSearchParams(location.search)
  const postIdToScroll = query.get("postId")

  const { user } = useContext(AuthContext)
  const [profile, setProfile] = useState(null)
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [isFollowing, setIsFollowing] = useState(false)
  const [isEditModalOpen, setIsEditModalOpen] = useState(false)

  const postRefs = useRef({})

  const isOwnProfile = user && profile && user.id === profile.id

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true)
        const res = await axios.get(`${BASE_URL}/api/users/${username}`)
        setProfile(res.data)
        setIsFollowing(res.data.isFollowing)

        const postsRes = await axios.get(`${BASE_URL}/api/posts/user/${username}`)
        setPosts(postsRes.data)

        setLoading(false)
      } catch (error) {
        setError("Error al cargar el perfil")
        setLoading(false)
        console.error("Error fetching profile:", error)
      }
    }

    fetchProfile()
  }, [username])

  // Scroll automÃ¡tico al post si existe postId en URL
  useEffect(() => {
    if (postIdToScroll && postRefs.current[postIdToScroll]) {
      postRefs.current[postIdToScroll].scrollIntoView({ behavior: "smooth", block: "start" })
    }
  }, [postIdToScroll, posts])

  const handleFollow = async () => {
    try {
      if (isFollowing) {
        await axios.delete(`${BASE_URL}/api/users/${profile.id}/unfollow`)
      } else {
        await axios.post(`${BASE_URL}/api/users/${profile.id}/follow`)
      }
      setIsFollowing(!isFollowing)
      setProfile({
        ...profile,
        followers: isFollowing ? profile.followers - 1 : profile.followers + 1,
      })
    } catch (error) {
      console.error("Error following/unfollowing user:", error)
    }
  }

  const handleProfileUpdate = (updatedData) => {
    setProfile({
      ...profile,
      ...updatedData,
    })
  }

  if (loading) return <div className="loading">Cargando perfil...</div>
  if (error) return <div className="error">{error}</div>
  if (!profile) return <div className="error">Usuario no encontrado</div>

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-image-container">
          <img
            src={profile.profileImage ? `${BASE_URL}${profile.profileImage}` : "/placeholder.svg?height=150&width=150"}
            alt={profile.username}
            className="profile-image"
          />
          {isOwnProfile && (
            <button className="edit-profile-image-button" onClick={() => setIsEditModalOpen(true)}>
              Cambiar foto
            </button>
          )}
        </div>
        <div className="profile-info">
          <div className="profile-username-container">
            <h1 className="profile-username">{profile.username}</h1>
            {isOwnProfile && (
              <button className="edit-profile-button" onClick={() => setIsEditModalOpen(true)}>
                Editar perfil
              </button>
            )}
          </div>
          <div className="profile-stats">
            <div className="profile-stat">
              <span className="stat-count">{profile.posts}</span>
              <span className="stat-label">Publicaciones</span>
            </div>
            <div className="profile-stat">
              <span className="stat-count">{profile.followers}</span>
              <span className="stat-label">Seguidores</span>
            </div>
            <div className="profile-stat">
              <span className="stat-count">{profile.following}</span>
              <span className="stat-label">Siguiendo</span>
            </div>
            <div className="profile-stat">
              <span className="stat-count">{profile.comments}</span>
              <span className="stat-label">Comentarios</span>
            </div>
            <div className="profile-stat">
              <span className="stat-count">{profile.likes}</span>
              <span className="stat-label">Me gusta</span>
            </div>
          </div>

          {user && user.id !== profile.id && (
            <div className="profile-actions">
              <button className={`follow-button ${isFollowing ? "following" : ""}`} onClick={handleFollow}>
                {isFollowing ? "Dejar de Seguir" : "Seguir"}
              </button>
            </div>
          )}
        </div>
      </div>
      <div className="profile-bio">
        <p>{profile.bio || "Sin biografÃ­a"}</p>
      </div>
      <div className="profile-posts">
        <h2>Publicaciones</h2>
        <div className="posts-container">
          {posts.length > 0 ? (
            posts.map((post) => (
              <div
                key={post.id}
                ref={(el) => {
                  postRefs.current[post.id] = el
                }}
              >
                <Post post={post} />
              </div>
            ))
          ) : (
            <p>No hay publicaciones disponibles.</p>
          )}
        </div>
      </div>

      {isOwnProfile && (
        <EditProfileModal
          isOpen={isEditModalOpen}
          onClose={() => setIsEditModalOpen(false)}
          profile={profile}
          onProfileUpdate={handleProfileUpdate}
        />
      )}
    </div>
  )
}

export default Profile
