"use client"

import { useState } from "react"
import { useNavigate } from "react-router-dom"
import axios from "axios"

const CreatePost = () => {
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const [imagePreview, setImagePreview] = useState(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const navigate = useNavigate()

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setImage(file)
      const reader = new FileReader()
      reader.onloadend = () => {
        setImagePreview(reader.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!content.trim() && !image) {
      setError("Por favor, añada texto o una imagen a su publicación")
      return
    }

    setLoading(true)
    setError("")

    try {
      const formData = new FormData()
      formData.append("content", content)
      if (image) {
        formData.append("image", image)
      }

      await axios.post("http://localhost:5000/api/posts", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      setLoading(false)
      navigate("/")
    } catch (error) {
      setLoading(false)
      setError("Error al crear la publicación")
      console.error("Error creating post:", error)
    }
  }

  return (
    <div className="create-post-container">
      <h1>Crear Nueva Publicación</h1>
      {error && <div className="create-post-error">{error}</div>}
      <form onSubmit={handleSubmit} className="create-post-form">
        <div className="form-group">
          <label htmlFor="content">¿Qué estás pensando?</label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Escribe algo..."
            rows="4"
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="image">Añadir Imagen</label>
          <input type="file" id="image" accept="image/*" onChange={handleImageChange} />
          {imagePreview && (
            <div className="image-preview">
              <img src={imagePreview || "/placeholder.svg"} alt="Preview" />
            </div>
          )}
        </div>
        <button type="submit" className="create-post-button" disabled={loading}>
          {loading ? "Publicando..." : "Publicar"}
        </button>
      </form>
    </div>
  )
}

export default CreatePost
