"use client"

import { useState, useContext } from "react"
import { Link } from "react-router-dom"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import CommentList from "./CommentList"
import Avatar from "./Avatar"
import EditPostModal from "./EditPostModal" // Importa el modal de edición

const Post = ({ post, onPostUpdate }) => {
  const { user } = useContext(AuthContext)
  const [currentPost, setCurrentPost] = useState(post) // Estado local para el post editable
  const [likes, setLikes] = useState(post.likes || 0)
  const [liked, setLiked] = useState(post.liked || false)
  const [showComments, setShowComments] = useState(false)
  const [commentText, setCommentText] = useState("")
  const [comments, setComments] = useState(post.comments || [])
  const [commentCount, setCommentCount] = useState(post.commentCount || post.comments?.length || 0)
  const [loadingComments, setLoadingComments] = useState(false)
  const [allCommentsLoaded, setAllCommentsLoaded] = useState(false)
  const [imageError, setImageError] = useState(false)
  const [showEditModal, setShowEditModal] = useState(false)

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null
    if (imagePath.startsWith("http")) return imagePath
    return `http://localhost:5000${imagePath}`
  }

  const handlePostUpdate = (updatedPost) => {
    setImageError(false)
    setCurrentPost(updatedPost) // Actualiza estado local con post editado
    if (onPostUpdate) {
      onPostUpdate(updatedPost) // Notifica al padre si existe callback
    } else {
      console.warn("onPostUpdate no está definido")
    }
  }

  const handleLike = async () => {
    try {
      await axios.post(`http://localhost:5000/api/posts/${currentPost.id}/like`)
      setLiked(!liked)
      setLikes(liked ? likes - 1 : likes + 1)
    } catch (error) {
      console.error("Error liking post:", error)
    }
  }

  const handleComment = async (e) => {
    e.preventDefault()
    if (!commentText.trim()) return
    try {
      const res = await axios.post(`http://localhost:5000/api/posts/${currentPost.id}/comment`, {
        content: commentText,
      })
      const newComment = {
        ...res.data,
        user: {
          ...res.data.user,
          profileImage: res.data.user.profileImage,
        },
      }
      setComments([newComment, ...comments])
      setCommentCount(commentCount + 1)
      setCommentText("")
    } catch (error) {
      console.error("Error commenting on post:", error)
    }
  }

  const toggleComments = async () => {
    setShowComments(!showComments)
    if (showComments) return
    if (comments.length === 0 && !showComments) {
      try {
        setLoadingComments(true)
        const res = await axios.get(`http://localhost:5000/api/posts/${currentPost.id}/comments?limit=5`)
        setComments(res.data.comments)
        setAllCommentsLoaded(res.data.comments.length >= res.data.pagination.total)
        setLoadingComments(false)
      } catch (error) {
        console.error("Error loading comments:", error)
        setLoadingComments(false)
      }
    }
  }

  const loadMoreComments = async () => {
    try {
      setLoadingComments(true)
      const page = Math.floor(comments.length / 5) + 1
      const res = await axios.get(`http://localhost:5000/api/posts/${currentPost.id}/comments?page=${page}&limit=5`)
      setComments([...comments, ...res.data.comments])
      setAllCommentsLoaded(comments.length + res.data.comments.length >= res.data.pagination.total)
      setLoadingComments(false)
    } catch (error) {
      console.error("Error loading more comments:", error)
      setLoadingComments(false)
    }
  }

  const handleImageError = () => {
    console.error("Error al cargar la imagen:", currentPost.image)
    setImageError(true)
  }

  // Funciones para controlar modal de edición
  const handleOpenModal = () => setShowEditModal(true)
  const handleCloseModal = () => setShowEditModal(false)

  // Guardar cambios desde el modal
  const handleSaveEdit = (updatedPost) => {
    handlePostUpdate(updatedPost)
    setShowEditModal(false)
  }

  return (
    <div className="post">
      <div className="post-header">
        <Link to={`/profile/${currentPost.user.username}`} className="post-user">
          <Avatar
            src={currentPost.user.profileImage}
            username={currentPost.user.username}
            size={40}
            className="post-user-image"
          />
          <span className="post-username">{currentPost.user.username}</span>
        </Link>
        <span className="post-date">{new Date(currentPost.createdAt).toLocaleDateString()}</span>
        {/* Botón para abrir modal de edición (visible solo si es el dueño del post) */}
        {user?.id === currentPost.user.id && (
          <button onClick={handleOpenModal} className="edit-post-button">
            Editar
          </button>
        )}
      </div>
      <div className="post-content">
        <p>{currentPost.content}</p>
        {currentPost.image && !imageError && (
          <div className="post-image-container">
            <img
              src={getImageUrl(currentPost.image) || "/placeholder.svg"}
              alt="Post"
              className="post-image"
              onError={handleImageError}
            />
          </div>
        )}
        {imageError && (
          <div className="post-image-error">
            <p>No se pudo cargar la imagen</p>
            <p className="post-image-path">Ruta: {currentPost.image}</p>
          </div>
        )}
      </div>
      <div className="post-actions">
        <button className={`post-like-button ${liked ? "liked" : ""}`} onClick={handleLike}>
          {liked ? "❤️" : "🤍"} {likes}
        </button>
        <button className="post-comment-button" onClick={toggleComments}>
          💬 {commentCount}
        </button>
      </div>
      {showComments && (
        <div className="post-comments">
          <form onSubmit={handleComment} className="comment-form">
            <input
              type="text"
              placeholder="Escribe un comentario..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              className="comment-input"
            />
            <button type="submit" className="comment-submit">
              Enviar
            </button>
          </form>

          {loadingComments && comments.length === 0 ? (
            <div className="loading">Cargando comentarios...</div>
          ) : (
            <>
              <CommentList comments={comments} />

              {!allCommentsLoaded && commentCount > comments.length && (
                <div className="view-all-comments">
                  <button className="view-all-comments-button" onClick={loadMoreComments} disabled={loadingComments}>
                    {loadingComments
                      ? "Cargando..."
                      : `Ver más comentarios (${commentCount - comments.length} restantes)`}
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      )}

      {/* Modal de edición */}
      {showEditModal && (
        <EditPostModal
          post={currentPost}
          onClose={handleCloseModal}
          onSave={handleSaveEdit}
        />
      )}
    </div>
  )
}

export default Post
