import { useState, useEffect, useRef } from "react"
import axios from "axios"
import Post from "../components/Post"

const Home = () => {
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [page, setPage] = useState(1)
  const [totalPages, setTotalPages] = useState(1)
  const postRefs = useRef({})  // Aquí guardamos refs por post id

  const fetchPosts = async () => {
    try {
      setLoading(true)
      const res = await axios.get(`http://localhost:5000/api/posts?page=${page}&limit=10`)
      setPosts(res.data.posts)
      setTotalPages(res.data.pagination.totalPages)
      setLoading(false)
    } catch (error) {
      console.error("Error fetching posts:", error)
      setError("Error al cargar las publicaciones")
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchPosts()
  }, [page])

  const handlePostUpdate = (updatedPost) => {
    setPosts(prevPosts => prevPosts.map((post) =>
      post.id === updatedPost.id ? { ...post, ...updatedPost } : post
    ))

    // Scroll al post actualizado:
    const postElement = postRefs.current[updatedPost.id]
    if (postElement) {
      postElement.scrollIntoView({ behavior: "smooth", block: "start" })
    }
  }

  const handlePostDelete = (postId) => {
    setPosts(prevPosts => prevPosts.filter((post) => post.id !== postId))
  }

  const handlePrevPage = () => {
    if (page > 1) {
      setPage(page - 1)
    }
  }

  const handleNextPage = () => {
    if (page < totalPages) {
      setPage(page + 1)
    }
  }

  if (loading && posts.length === 0) {
    return <div className="loading">Cargando publicaciones...</div>
  }

  if (error) {
    return <div className="error">{error}</div>
  }

  return (
    <div className="home-container">
      <h1>Inicio</h1>
      <div className="posts-container">
        {posts.length > 0 ? (
          posts.map((post) => (
            <div
              key={post.id}
              ref={el => postRefs.current[post.id] = el} // Aquí asignamos el ref
            >
              <Post
                post={post}
                onPostUpdate={handlePostUpdate}
                onPostDelete={handlePostDelete}
              />
            </div>
          ))
        ) : (
          <p className="no-posts">No hay publicaciones disponibles.</p>
        )}
      </div>
      {totalPages > 1 && (
        <div className="pagination">
          <button className="pagination-button" onClick={handlePrevPage} disabled={page === 1}>
            Anterior
          </button>
          <span className="pagination-info">
            Página {page} de {totalPages}
          </span>
          <button className="pagination-button" onClick={handleNextPage} disabled={page === totalPages}>
            Siguiente
          </button>
        </div>
      )}
    </div>
  )
}

export default Home
