// client/src/components/Navbar.jsx
import { useContext, useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import SearchBar from "./SearchBar"
import NotificationList from "./NotificationList"
import axios from "axios"

const Navbar = () => {
  const { user, logout, token } = useContext(AuthContext)
  const navigate = useNavigate()
  const [notifications, setNotifications] = useState([])

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const res = await axios.get("/notifications", {
          headers: { Authorization: `Bearer ${token}` },
        })
        setNotifications(res.data)
      } catch (error) {
        console.error("Error al obtener notificaciones:", error)
      }
    }

    if (user && token) {
      fetchNotifications()
    }
  }, [user, token])

  const handleLogout = () => {
    logout()
    navigate("/login")
  }

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          saveyoursoul
        </Link>

        {user && <SearchBar />}

        {user ? (
          <ul className="navbar-menu">
            <li className="navbar-item">
              <Link to="/" className="navbar-link">Inicio</Link>
            </li>
            <li className="navbar-item">
              <Link to="/create-post" className="navbar-link">Crear Publicación</Link>
            </li>
            <li className="navbar-item">
              <Link to={`/profile/${user.username}`} className="navbar-link">Perfil</Link>
            </li>
            <li className="navbar-item">
              {/* ✅ Pasa las props aquí */}
              <NotificationList notifications={notifications} token={token} />
            </li>
            <li className="navbar-item">
              <button onClick={handleLogout} className="navbar-button">
                Cerrar Sesión
              </button>
            </li>
          </ul>
        ) : (
          <ul className="navbar-menu">
            <li className="navbar-item">
              <Link to="/login" className="navbar-link">Iniciar Sesión</Link>
            </li>
            <li className="navbar-item">
              <Link to="/register" className="navbar-link">Registrarse</Link>
            </li>
          </ul>
        )}
      </div>
    </nav>
  )
}

export default Navbar
