"use client"

import { useState, useEffect, useContext } from "react"
import axios from "axios"
import { AuthContext } from "../context/AuthContext"
import Post from "../components/Post"

const Home = () => {
  const { updatePost, deletePost: deletePostFromContext } = useContext(AuthContext)

  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [page, setPage] = useState(1)
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 5,
    total: 0,
    totalPages: 0,
  })

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        setLoading(true)
        const res = await axios.get(`http://localhost:5000/api/posts?page=${page}&limit=5`)
        setPosts(res.data.posts)
        setPagination(res.data.pagination)
        setLoading(false)
      } catch (error) {
        console.error("Error fetching posts:", error)
        setError("Error al cargar las publicaciones")
        setLoading(false)
      }
    }

    fetchPosts()
  }, [page])

  // ✅ Función para manejar actualización de posts
  const handlePostUpdate = (updatedPost) => {
    setPosts((prevPosts) => prevPosts.map((post) => (post.id === updatedPost.id ? { ...post, ...updatedPost } : post)))
    updatePost(updatedPost.id, updatedPost)
  }

  // ✅ Función para manejar eliminación de posts
  const handlePostDelete = (postId) => {
    setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId))
    deletePostFromContext(postId)
    setPagination((prev) => ({
      ...prev,
      total: prev.total - 1,
    }))
  }

  const handlePrevPage = () => {
    if (page > 1) {
      setPage(page - 1)
    }
  }

  const handleNextPage = () => {
    if (page < pagination.totalPages) {
      setPage(page + 1)
    }
  }

  if (loading && page === 1) {
    return <div className="loading">Cargando publicaciones...</div>
  }

  if (error) {
    return <div className="error">{error}</div>
  }

  return (
    <div className="home-container">
      <h1>¿Que hay de nuevo?</h1>
      <div className="posts-container">
        {posts.length > 0 ? (
          posts.map((post) => (
            <Post key={post.id} post={post} onPostUpdate={handlePostUpdate} onPostDelete={handlePostDelete} />
          ))
        ) : (
          <p>No hay publicaciones disponibles. ¡Sigue a más usuarios o crea tu primera publicación!</p>
        )}
      </div>

      {pagination.totalPages > 1 && (
        <div className="pagination">
          <button className="pagination-button" onClick={handlePrevPage} disabled={page === 1 || loading}>
            Anterior
          </button>
          <div className="pagination-info">
            Página {page} de {pagination.totalPages}
          </div>
          <button
            className="pagination-button"
            onClick={handleNextPage}
            disabled={page === pagination.totalPages || loading}
          >
            Siguiente
          </button>
        </div>
      )}
    </div>
  )
}

export default Home
