"use client";

import { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import "../App.css";

const API = "http://localhost:5000";   //  ◀️  cambia aquí si tu backend usa otro host/puerto

const AdminUsers = () => {
  const { isSuperAdmin } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  /* ──────────────────────────────── */
  useEffect(() => {
    fetchUsers();
  }, []);
  /* ──────────────────────────────── */

  async function fetchUsers() {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch(`${API}/api/admin/users`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) {
        console.error("Respuesta no válida:", res.status, await res.text());
        return;
      }
      const data = await res.json(); // ← es un ARRAY plano
      setUsers(data);
    } catch (err) {
      console.error("Error fetching users:", err);
    } finally {
      setLoading(false);
    }
  }

  /* ──────────────────────────────── */
  async function deleteUser(id, username) {
    if (!window.confirm(`¿Eliminar a "${username}"? Esta acción no se puede deshacer.`))
      return;

    try {
      const token = localStorage.getItem("token");
      const res = await fetch(`${API}/api/admin/users/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.ok) {
        await fetchUsers();           // refresca la lista
        alert("Usuario eliminado");
      } else {
        const err = await res.json();
        alert(`Error: ${err.message || res.statusText}`);
      }
    } catch (err) {
      console.error("Error deleting user:", err);
      alert("Error al eliminar el usuario");
    }
  }
  /* ──────────────────────────────── */

  if (loading) return <div className="admin-loading">Cargando usuarios…</div>;

  return (
    <div className="admin-users">
      <div className="admin-header">
        <h1>Gestión de Usuarios</h1>
        <p>Total: {users.length}</p>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Usuario</th>
              <th>Email</th>
              <th>Rol</th>
              <th>Fecha registro</th>
              <th>Acciones</th>
            </tr>
          </thead>

          <tbody>
            {users.map(u => (
              <tr key={u.id}>
                <td>{u.id}</td>
                <td>{u.username}</td>
                <td>{u.email}</td>
                <td><span className={`role-badge ${u.role}`}>{u.role}</span></td>
                <td>{new Date(u.created_at).toLocaleDateString()}</td>
                <td>
                  {isSuperAdmin() && u.role !== "admin" && (
                    <button className="action-btn delete" onClick={() => deleteUser(u.id, u.username)}>
                      Eliminar
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminUsers;
